import matplotlib.pyplot as plt

def plot_turbine_coordinates(turbine_coords, xlim, ylim):
    """
    Plot turbine coordinates on a graph.

    :param turbine_coords: 2D list of turbine coordinates, e.g. [[x1, y1], [x2, y2], ...]
    :param xlim: Tuple of x-axis limits, e.g. (xmin, xmax)
    :param ylim: Tuple of y-axis limits, e.g. (ymin, ymax)
    """
    # Unzip the coordinates into x and y lists
    x_coords, y_coords = zip(*turbine_coords)

    # Create a scatter plot of the turbine coordinates
    plt.scatter(x_coords, y_coords, c='steelblue', marker='o', s=10,)

    # Set x and y axis limits
    plt.xlim(xlim)
    plt.ylim(ylim)

    # Set labels and title
    plt.xlabel('Width')
    plt.ylabel('Height')
    plt.title('Turbine Coordinates on Wind Farm')
    farm_width = 7000
    farm_height = 14000


    # Show the legend and the plot
    plt.show()

